/** * List add to cart buttons */var CartButtonsLists = (function(superClass) {	extend(CartButtonsLists, superClass);	function CartButtonsLists() {		var self = this;		CartButtonsLists.__super__.constructor.call(this);		$(document).on('click', '.js_change_model', function () {			self.changeModel($(this));		});		$(document).on('click', '.js_add_in_cart', this.addInCartClick.bind(this));		$(document).on('mouseleave', '.js_item', function (e) {			self.deactivateForms();		});		$('.js_model_form').hide();		$('.js_item').each(function () {			$(this).find('.js_model_form').first().show();		});	}	// Show model dependencies	CartButtonsLists.prototype.changeModel = function ($button) {		var $forms = $button.closest('.js_item').find('.js_model_form');		$forms.hide();		$forms.filter('[data-model-id="' + $button.data('model-id') + '"]').show();	};	CartButtonsLists.prototype.deactivateForms = function () {		$('.js_model_form').removeClass('active');		$('.darkness').removeClass('active');	};	CartButtonsLists.prototype.addInCartClick = function (e) {		var $form = $(e.target).closest('.js_model_form');		if(!$form.hasClass('active')) {			$form.addClass('active');			$('.darkness').addClass('active');			return false;		}	};	return CartButtonsLists;})(CartButtons);$(window).ready(function () {	new CartButtonsLists();});