$(window).ready(function () {    $(document).on('change', '#create_order_np_delivery_city, #create_order_del_delivery_city', function () {        var city_id = $(this).val();        var next_select = $(this).parent().next().find('select.select');        $.ajax({            url: Routing.generate('get_stores_by_city'),            data: {                city_id: city_id,            },            dataType: "json",            type: "POST",            success: function (data) {                if(data.status =='OK'){                    next_select.empty();                    $.each( data.stores, function( key, value ) {                        next_select.append(new Option(value.name, value.id));                    });                    return false;                }else{                    console.log(data);                }            },            error: function (message) {                console.log(message.status + ' ' + message.statusText);            },        });    });});