/** * List add to cart buttons */var ProductCartButton = (function(superClass) {	extend(ProductCartButton, superClass);	function ProductCartButton() {		return ProductCartButton.__super__.constructor.apply(this, arguments);	}	ProductCartButton.prototype.successSubmit = function ($item, data) {		ProductCartButton.__super__.successSubmit.call(this, $item, data);		$item.find('.select').select2('data', null);	};	ProductCartButton.prototype.errorSubmit = function ($item, data) {		$.each(data.responseJSON.errors, function (field, messages) {			$item.find('select.select').each(function() {				var $this = $(this);				if($this.attr('name').match(new RegExp(field))) {					$('#s2id_' + $this.attr('id')).addClass('error');				}			});		});	}; 	return ProductCartButton;})(CartButtons);$(window).ready(function () {	new ProductCartButton();});